grid(axes1,'on');
% Set the remaining axes properties
set(axes1,'FontSize',19);
% Create legend
legend1 = legend(axes1,'show');
set(legend1,...
    'Position',[0.153176653342321 0.75551087386456 0.136979163375994 0.135906036401488]);

